import React , {Component} from "react";
import ReactDOM from 'react-dom'
import { useEffect , useState } from "react";

// COMPONENTS
import Loading from "components/Loading/Loading";

// CSS
import "../assets/css/producto.css";

// HELPERS
import { valoracion } from "helpers/functions.js";

function ProductosLista() {
	
	const [ productos , setProductos ] = useState();
	const [ loading , setLoading ] = useState( false );

	useEffect(() => {
		setLoading( true )
		var api = "https://fakestoreapi.com/products";
		fetch( api ).then(value => value.json())
		.then( x => {
			// console.log( x[0] );
			var todo = [];
			for ( var count = 0; count <= x.length-1; count++ ) {
				let resumen = x[ count ];
				var producto = {
					"id" : resumen.id ,
					"nombre" : resumen.title ,
					"precio" : function(){
						let precio = resumen.price;
						precio = precio * 918;
						precio = String(precio).split( "." )[0];
						precio = Number(precio).toLocaleString('es-CL');
						return precio;
					} ,
					"imagen" : resumen.image ,
					"valoracion" : {
						"rating" : resumen.rating.rate ,
					} ,
				};
				todo.push({
					"id" : producto.id ,
					"nombre" : producto.nombre ,
					"precio" : producto.precio() ,
					"imagen" : producto.imagen ,
					"rating" : producto.valoracion.rating ,
				})
			};
			setProductos( todo )
			setLoading( false )
		});
	},[])

	function Producto() {
		return (
			<>
				<div className="productos">
					{
						productos?.map((prod) => {
							return(
								<div className="producto" key={ prod.id }>
									<div className="imagen">
										<img src={ prod.imagen } alt={ prod.nombre } />
									</div>
									<h4>{ prod.nombre }</h4>
									<p className="precio">$ { prod.precio }</p>
									<p className="valoracion">{ valoracion( prod.rating  )}</p>
								</div>
							)
						})
					}
				</div>
			</>
		)
	}
	
	if ( loading ) {
		return (
			<section className="test">
				{/* <HelmetResumen title="Cargando Color ..." />
				<h2>
					<Icono nombre="palette" />
					Color Random
				</h2> */}
				<Loading />
			</section>
		);
	};
	
	return (
		<Producto />
	)
	
}

export default ProductosLista;
