import { useEffect } from "react";
import { useParams } from 'react-router-dom';


function Categoria() {
	
	let { slug } = useParams();
	console.log( `SLUUUUUUG => ${ slug }` );
	let categoria = "";
	if ( slug === "joyeria" ) {
		categoria = "jewerly"
	} else if ( slug ) {}
	
	switch ( slug ) {
		case "joyeria":
			categoria = "jewelery";
			break;
		case "electronica":
			categoria = "electronics";
			break;
		case "hombre":
			categoria = "men's clothing";
			break;
		case "mujer":
			categoria = "women's clothing";
			break;
	
		default:
			alert( `Categoria: ${ categoria } no reconocida.` );
			break;
	}
	
	useEffect(() => {
		
		const api = `https://fakestoreapi.com/products/category/${ categoria }`;
		console.log( api );
		fetch( api ).then( api => api.json() )
		.then( x => {
			console.log( x );
		});
	},[categoria])

	return (
		<section id="categoria">
			<h1>Categoria</h1>
		</section>
	)
}
export default Categoria;
