import { useEffect } from "react";
import { useParams } from 'react-router-dom';


function Categoria() {
	
	let { slug } = useParams();
	console.log( `SLUUUUUUG => ${ slug }` );
	
	useEffect(() => {
		let categoria = "";
		if ( slug === "joyeria" ) {
			categoria = "jewerly"
		} else if ( slug ) {}
		
		switch ( slug ) {
			case "joyeria":
				categoria = "jewerly";
				break;
		
			default:
				alert( `Categoria: ${ categoria } no reconocida.` );
				break;
		}
		
		const api = `https://fakestoreapi.com/products/category/${ categoria }`
	},[])

	return (
		<section id="categoria">
			<h1>Categoria</h1>
		</section>
	)
}
export default Categoria;
