const valoracion = function ( estrellas ) {
	estrellas = String( estrellas )
	if ( estrellas === "1" ) {
		return (
			<span className="material-symbols-outlined">star</span>
		)
	} else if ( estrellas.includes( "1." ) ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star_half</span>
			</>
		)
	} else if ( estrellas === "2" ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
			</>
		)
	} else if ( estrellas.includes( "2." ) ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star_half</span>
			</>
		)
	} else if ( estrellas === "3" ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
			</>
		)
	} else if ( estrellas.includes( "3." ) ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star_half</span>
			</>
		)
	} else if ( estrellas === "4" ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
			</>
		)
	} else if ( estrellas.includes( "4." ) ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star_half</span>
			</>
		)
	} else if ( estrellas === "5" ) {
		return (
			<>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
				<span className="material-symbols-outlined">star</span>
			</>
		)
	} else {
		alert( `Estrella ${ estrellas } no seteada.` );
	}
	
};

const pillCategoryNombre = function ( slug ) {

	switch ( slug ) {
		case "jewelery":
			slug = "Joyeria";
			// console.log( `slug: ${slug}` );
			return slug;
		case "electronics":
			slug = "Electrónica";
			// console.log( `slug: ${slug}` );
			return slug;
		case "men's clothing":
			slug = "Hombre";
			// console.log( `slug: ${slug}` );
			return slug;
		case "women's clothing":
			slug = "Mujer";
			// console.log( `slug: ${slug}` );
			return slug;
	
		default:
			return `Categoria no reconocida [${slug}]`
	}
};

export { valoracion , pillCategoryNombre }
