import { useEffect , useState } from "react";
import { useParams } from 'react-router-dom';


function Categoria() {
	
	let { slug } = useParams();
	
	let categoria = "";
	switch ( slug ) {
		case "joyeria":
			categoria = "jewelery";
			break;
		case "electronica":
			categoria = "electronics";
			break;
		case "hombre":
			categoria = "men's clothing";
			break;
		case "mujer":
			categoria = "women's clothing";
			break;
	
		default:
			alert( `Categoria: ${ categoria } no reconocida.` );
			break;
	}
	
	const [ producto , setProducto ] = useState();
	
	useEffect(() => {
		
		const api = `https://fakestoreapi.com/products/category/${ categoria }`;
		// console.log( api );
		fetch( api ).then( api => api.json() )
		.then( get => {
			var todo = [];
			console.log( get );
			for ( var count = 0; count <= get.length-1; count++ ) {
				var productito = {
					"id" : get[ count ].id ,
					"nombre" : get[ count ].title ,
					"precio" : get[ count ].price ,
					"imagen" : get[ count ].image ,
				};
				todo.push({
					"id" : productito.id ,
					"nombre" : productito.nombre ,
					"precio" : productito.precio ,
					"imagen" : productito.imagen ,
				});
			};
			console.log( productito );
			console.log( todo );
			setProducto( todo )
		});
	},[categoria])

	function ListadeProductos() {
		return (
			<>
				{
					producto?.map((p) => {
						return(
							<div className="producto" key={ p.id }>
								<h3>{ p.nombre }</h3>
								<p>{ p.precio }</p>
							</div>
						)
					})
				}
			</>
		)
	}

	return (
		<section className="productos">
			<ListadeProductos />
		</section>
	)
}
export default Categoria;
