import { useEffect , useState } from "react";
import { useParams } from 'react-router-dom';

function ProductoDetalle() {
	
	const [ producto , setProducto ] = useState();
	let { slug } = useParams();

	useEffect(() => {
		const api = `https://fakestoreapi.com/products/${ slug }`;
		fetch( api ).then( api => api.json() )
		.then( x => {
			var producto = {
				"id" : x.id ,
				"nombre" : x.title ,
				"precio" : x.price ,
				"descripcion" : x.description ,
				"categoria" : x.category ,
				"imagen" : x.image ,
				"valoracion" : {
					"rating" : x.rating.rate ,
					"cantidad" : x.rating.count ,
				} ,
			}
			// var todo = [{
			// 	"id" : producto.id ,
			// 	"nombre" : producto.nombre ,
			// 	"precio" : producto.precio ,
			// 	"descripcion" : producto.descripcion ,
			// 	"categoria" : producto.categoria ,
			// 	"imagen" : producto.imagen ,
			// 	"valoracion" : {
			// 		"rating" : producto.valoracion.rating ,
			// 		"cantidad" : producto.valoracion.cantidad ,
			// 	}
			// }];
			var todo = [{
				"id" : producto.id ,
				"nombre" : producto.nombre ,
				"precio" : producto.precio ,
				"descripcion" : producto.descripcion ,
				"categoria" : producto.categoria ,
				"imagen" : producto.imagen ,
				"valoracion" : {
					"rating" : producto.valoracion.rating ,
					"cantidad" : producto.valoracion.cantidad ,
				}
			}];
			setProducto( todo );
		})
	},[slug])

	function Detalle() {
		return (
			<>
				{
					producto?.map((p) => {
						return(
							<div className="producto" key={ p.id }>
								<img src={ p.imagen } alt={ p.nombre } />
								<h4>{ p.nombre }</h4>
							</div>
						)
					})
				}
			</>
		)
	}

	return (
		<section>
			<Detalle />
		</section>
	)
}
export default ProductoDetalle;
